mapper.insert:
  		INSERT INTO MEMBER ( 
  		         USER_NO
                ,EMAIL
                ,USER_PASSWORD
                ,USER_NICKNAME
                ,ADDRESS)
		VALUES (
               SEQ_MNO.NEXTVAL
              ,#{email}
        	  ,#{userPassword}
        	  ,#{userNickName}
        	  ,#{address}
		)

mapper.select:
  			SELECT count(*)
			FROM MEMBER 
			WHERE USER_NICKNAME= #{userNickname}

  			SELECT count(*)
			FROM MEMBER 
			WHERE EMAIL= #{email}
