mapper.insert:
  		INSERT INTO MEMBER ( 
  		         USER_NO
                ,EMAIL
                ,USER_PASSWORD
				,USER_NAME
                ,USER_NICKNAME
                ,ADDRESS
                ,CATEGORY_NO
                ,GENDER)
		VALUES (
               SEQ_MNO.NEXTVAL
              ,#{email}
        	  ,#{userPassword}
        	  ,#{userName}
        	  ,#{userNickName}
        	  ,#{address}
              ,#{categoryNo}
              ,#{gender}
		)

mapper.select:
  			SELECT count(*)
			FROM MEMBER 
			WHERE USER_NICKNAME= #{userNickname}

  			SELECT count(*)
			FROM MEMBER 
			WHERE EMAIL= #{email}

  			SELECT count(*)
			FROM MEMBER 
			WHERE EMAIL= #{email}

mapper.update:
  			UPDATE MEMBER
			SET USER_PASSWORD = #{userPassword}
			WHERE EMAIL = #{email}

  			UPDATE MEMBER
			SET USER_NICKNAME = #{userNickName}
			   ,GENDER = #{gender}
			WHERE EMAIL = #{email}
			AND STATUS = 'Y'
