mapper.insert:
INSERT INTO ATTACHMENT(AT_NO ,ORIGIN_NAME ,CHANGE_NAME ,UPLOAD_DATE ,FILE_TYPE ,REF_NO) SELECT SEQ_ATT.NEXTVAL, A.* FROM (
SELECT #{item.originName} AS ORIGIN_NAME ,#{item.changeName} AS CHANGE_NAME ,SYSDATE ,#{item.fileType} AS FILE_TYPE ,#{item.refNo} AS REF_NO FROM DUAL
) A

